mapper.select:
select * from store
where cr_num in(
	select cr_num
	from owner_store
	where owner_id = #{ownerId}
)

mapper.update:
update store
set order_cnt = order_cnt + 1
where cr_num = #{crNum}

update store
set review_cnt = #{reviewCnt}
where cr_num = #{crNum}

update store
set review_cnt = #{reviewCnt}
where cr_num = #{crNum}
